package leetcode

import (
	"github.com/stretchr/testify/assert"
	"testing"
)

func TestLongestPalindrome(t *testing.T) {
	tt := []struct {
		input   string
		exceped string
	}{
		{
			"bb",
			"bb",
		},
		{
			"babad",
			"bab",
		},
		{
			"cbbd",
			"bb",
		},
		{
			"a",
			"a",
		},
		{
			"ac",
			"a",
		},
		{
			"",
			"",
		},
	}

	assertion := assert.New(t)
	for _, c := range tt {
		got := longestPalindrome(c.input)
		assertion.Equalf(c.exceped, got, "case#{%+v} got:{%v}", c, got)
	}
}
